import os, os.path, shutilfrom osgeo import ogrfrom osgeo import osrfrom osgeo import gdalsrcDatum = osr.SpatialReference()srcDatum.SetWellKnownGeogCS('NAD27')dstDatum = osr.SpatialReference()srcDatum.SetWellKnownGeogCS('WGS84')transform = osr.CoordinateTransformation(srcDatum, dstDatum)srcFile = ogr.Open(".shp")srcLayer = srcFile.getLayer(0)if os.path.exists(""):    shutil.rmtree("")os.mkdir("")driver = ogr.GetDriverByName("ESRI Shapefile")dstPath = os.path.join("","")dstFile = driver.CreateDataSource(dstPath)dstLayer = dstFile.CreateLayer("layer", dstDatum)for i in range(srcLayer.GetFeatureCount()):    feature = srcLayer.GetFeature(i)    geometry = feature.GetGeometryRef()    newGeometry = geometry.Clone()    newGeometry.Transform(transform)    feature = ogr.feature(dstLayer.GetLayerDefn())    feature.SetGeometry(newGeometry)    dstLayer.CreateFeature(feature)    feature.Destroy()srcFile.Destroy()dstFile.Destroy()